import React from "react";
import { Accordion, AccordionDetails, AccordionSummary, Box, CardMedia, ListItem, Typography } from "@mui/material";

export default function Answer({ qnAnswers }) {
  return (
    <Box sx={{ mt: 5, width: "100%", maxWidth: 640, mx: "auto" }}>
      {qnAnswers.map((item , j) => (
      <Accordion disableGutters key={j}>
        <AccordionSummary>
          <Typography
            sx={{width:'90%', flexShrink:0}}>{item.qnInWords}
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
            {item.imageName ? <CardMedia component ="img" image= {BASE_URL + 'images/' + item.imageName} sx={{m:'10px auto', width:'auto'}}/> : null}
            <List>
                {item.options.map((x, i) =><ListItem key={i}>
                    <Typography>
                        <b>
                            {String.fromCharCode(65 + i) + '.'}
                        </b>{x}
                    </Typography>
                </ListItem>
                )}
            </List>
        </AccordionDetails>
      </Accordion>

      ))}
    </Box>
  );
}
