import { Card, CardContent, Typography, List, ListItem, ListItemButton, CardHeader } from "@mui/material";
import React, { useEffect, useState } from "react";
import { createAPIEndpoint, ENDPOINTS } from "../api";

export default function Quiz() {
  const [qns, setQns] = React.useState([]);
  const [qnIndex, setQnIndex] = useState(0);

  const[startTimen, setStartTimen] = use

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.question)
      .fetch()
      .then((res) => {
        setQns(res.data);
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

return (
  qns.length !== 0 
      ? <Card sx={{maxWidth:640, mx:'auto',mt:5}}>
        <CardHeader title={'Question' + (qnIndex + 1) + 'of 5'}/>
        <CardContent>
          <Typography variant="h6">
            {qns[qnIndex].qinWords}
          </Typography>
          <List>
            {qns[qnIndex].options.map((item, idx)=>
            <ListItemButton key={idx} disableRipple>
              <div>
                <b>{String.fromCharCode(65 + idx) + " . "}</b>{item}
              </div>

            </ListItemButton>
            )}
          </List>

        </CardContent>
      </Card>
      : null
)
}
