import {
  Card,
  CardContent,
  Typography,
  List,
  ListItem,
  ListItemButton,
  CardHeader,
  Box,
} from "@mui/material";
import React, { useEffect, useState } from "react";
import { createAPIEndpoint, ENDPOINTS } from "../api";
import { getFormatedTime } from "../helper";

export default function Quiz() {
  const [qns, setQns] = React.useState([]);
  const [qnIndex, setQnIndex] = useState(0);

  let timer;
  const [timeTaken, setTimeTaken] = useState(0);

  const startTimer = () => {
    timer = setInterval(() => {
      setTimeTaken((prev) => prev + 1);
    }, [1000]);
  };

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.question)
      .fetch()
      .then((res) => {
        setQns(res.data);
        startTimer();
      })
      .catch((err) => console.log(err));
    return () => {
      clearInterval(timer);
    };
  }, []);

  return qns.length !== 0 ? (
    <Card
      sx={{
        maxWidth: 640,
        mx: "auto",
        mt: 5,
        "& . MultiCardHeader-action": { m: 0, alignSelf: "center" },
      }}
    >
      <CardHeader
        title={"Question" + (qnIndex + 1) + "of 5"}
        action={<Typography>{getFormatedTime(timeTaken)}</Typography>}
      />
      <Box></Box>
      <CardContent>
        <Typography variant="h6">{qns[qnIndex].qinWords}</Typography>
        <List>
          {qns[qnIndex].options.map((item, idx) => (
            <ListItemButton key={idx} disableRipple>
              <div>
                <b>{String.fromCharCode(65 + idx) + " . "}</b>
                {item}
              </div>
            </ListItemButton>
          ))}
        </List>
      </CardContent>
    </Card>
  ) : null;
}
